import React from "react";
import PersonList from "./EmployeeData";


handleSort = () => {
    const nameSort = this.state.results.sort((a, b) => {
        var nameA = a.first.toUpperCase();
        var nameB = b.first.toUpperCase();
        if (this.state.nameSort !== "des") {
            if (nameA < nameB) {
                return -1 ;
            } if (nameA > nameB) {
                return 1;
            }
            return 0;
        }
    })
}
function Table(props) {
    return (
        <div>
            <table className="table-container table is-fullwidth is-hoverable scheme-main-bis">
                <thead>
                    <tr>

                        <th  onClick={this.handleSort}>First Name</th>
                        <th>Last Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Employee ID</th>
                    </tr>
                </thead>

                <PersonList />
            
            </table>
        </div>
    )
}

export default Table;
