import React from 'react';
import API from '../Utils/API'

class SearchForm extends React.Container {

    state = {
        result
    }

employees = query => {
    API.search(query)
    .then(res => this.setState({result: res.data.results}))
    .catch(err => console.log(err))
}

filterNames = (search) => {
    var filterResult = this.state.result.filter(result => result.name.first === search)
    this.setState({
        result: filterResult
    })
    }

handleFormSubmit = event => {
    event.preventDefault();
    const value = event.target.value;
    const name = event.target.name;

    this.filterNames(value);
    this.filterNames(this.state.search);
};

handleInputChange = event => {
    event.preventDefault();
    const value = event.target.value;
    const name = event.target.name;

    this.setState({
        [name]: value
    })
}

render() {
    return (
        <div>
            <input value={this.state.search} handleFormSubmit={this.handleFormSubmit} handleInputChange={this.handleInputChange} className="input is-rounded is-normal" type="text" placeholder="Search Employee By Name" />
        </div>
    )
}
}
export default SearchForm;
