import React from 'react';
import Axios from 'axios';

class PersonList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            results: [],
            sortOrder: " "
        };

        handleSort = (whatToSort) => {
            console.log("test")
            let newSort 
            newSort = this.state.results.sort((a, b) => {
                if (this.state.sortOrder !== "des") {
                    return a[whatToSort]
                }
            })
        }
    }
    componentDidMount() {
        Axios.get('https://randomuser.me/api/?results=100&nat=us').then(res => {
            var results = res.data.results;
            this.setState({ results: results });
            console.log(results)

        })
    };


render() {
    return (
        <div>
        <table className="table-container table is-fullwidth is-hoverable scheme-main-bis">
            <thead>
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Employee ID</th>
                </tr>
            </thead>
            <tbody>
                {this.state.results.map(result => {
                    return <tr>
                        <td>{result.name.first}</td>
                        <td>{result.name.last}</td>
                        <td>{result.email}</td>
                        <td>{result.phone}</td>
                        <td>{result.id.value}</td>
                    </tr>;
                })}
            </tbody>
        </table>
        </div>
    );
}
}

export default PersonList;
