import React from 'react';
import Axios from 'axios';

class PersonList extends React.Component {
    constructor(props) {
        super(props);
    this.state = {
    results: []
};
   }
componentDidMount() {
    Axios.get('https://randomuser.me/api/?results=100&nat=us').then(res => {
        const results = res.data.results;
        this.setState({ results: results });
        console.log(results)

    })
};

handleColumnClick() {
    const {
        results: {results},
        sortParams
    } = this.state;
    const sortDirection = direction === "desc" ? "asc" : "desc";

    const sortedCollection = orderBy (
        results, ["First Name"], [sortDirection]
    );
    this.setState({
        results: sortedCollection,
        direction: sortDirection
    });
}


render() {
    return (
        <table className="table-container table is-fullwidth is-hoverable scheme-main-bis">
        <thead>
        <tr>
            <th onClick={this.handleSort}>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Employee ID</th>
        </tr>
    </thead>
        <tbody>
            {this.state.results.map(result => {
                return <tr>
                    <td>{result.name.first}</td>
                    <td>{result.name.last}</td>
                    <td>{result.email}</td>
                    <td>{result.phone}</td>
                    <td>{result.id.value}</td>
                </tr>;
            })}
        </tbody>
        </table>
    );
}
}
export default PersonList;
