import React from 'react';
import API from '../Utils/API';

class PersonList extends React.Component {
        state = {
            result: [],
            sortOrder: ""
        };

        componentDidMount() {
            this.resultEmployees("")
        }

        resultEmployees = query => {
            API.search(query)
            .then(res => this.setState({result: res.data.results}))
            .catch(err => console.log(err))
        }

        handleSort = () => {
                        let newSort
                            if (this.state.sortOrder !== "des") {
                               newSort = this.state.result.sort((a, b) => {
                                return a.results.id.value - b.results.id.value;
                            }) 
                        } else {
                            newSort = this.state.result.sort((a, b) => {
                                return b.results.id.value - a.results.id.value;
                            }) 
                        }
                        console.log(newSort)
                        const newSortOrder = this.state.sortOrder === "des" ? "asc" : "des"
                        this.setState({result: newSort, sortOrder: newSortOrder})
                    }

render() {
    return (
        <div>
        <table className="table-container table is-fullwidth is-hoverable scheme-main-bis">
            <thead>
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th onClick={() => this.handleSort()}>Employee ID</th>
                </tr>
            </thead>
            <tbody>
                {this.state.result.map(result => {
                    return <tr>
                        <td>{result.name.first}</td>
                        <td>{result.name.last}</td>
                        <td>{result.email}</td>
                        <td>{result.phone}</td>
                        <td>{result.id.value}</td>
                    </tr>;
                })}
            </tbody>
        </table>
        </div>
    );
}
}

export default PersonList;
